con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "This is my first groovy script"

log.info context.expand('${#TestCase#Name}')

log.info context.expand('${#TestCase#age}')

log.info testRunner.testCase.testSuite.testCases["Addemployee"].getPropertyValue("id")

log.info testRunner.testCase.testSuite.testCases["Getemployee"].getPropertyValue("age")

log.info testRunner.testCase.testSuite.testCases["Getemployee"].testSteps["get"].getPropertyValue("Request")

testRunner.testCase.testSuite.testCases["Addemployee"].setPropertyValue("department","CSE")

// automation script for getting the details to add employee


def reqxml = testRunner.testCase.testSuite.testCases["Addemployee"].testSteps["Add"].getPropertyValue("Request")
def xmladd = new XmlHolder(reqxml)

//xpath for all the node values
def xpathname = '//typ:addEmployee/typ:name'
def xpathid = '//typ:addEmployee/typ:id'
def xpathage = '//typ:addEmployee/typ:age'
def xpathdepartment = '//typ:addEmployee/typ:Department'


//value for all the xml nodes
def name = testRunner.testCase.testSuite.testCases["Addemployee"].getPropertyValue("name")
def id = testRunner.testCase.testSuite.testCases["Addemployee"].getPropertyValue("id")
def age = testRunner.testCase.testSuite.testCases["Addemployee"].getPropertyValue("age")
def department = testRunner.testCase.testSuite.testCases["Addemployee"].getPropertyValue("department")


xmladd.setNodeValue(xpathname,name)
xmladd.setNodeValue(xpathid,id)
xmladd.setNodeValue(xpathage,age)
xmladd.setNodeValue(xpathdepartment,department)
def newxml = xmladd.getXml();

testRunner.testCase.testSuite.testCases["Addemployee"].testSteps["Add"].setPropertyValue("Request",newxml)

//executing the request
def addTestStep = testRunner.testCase.testSuite.testCases["Addemployee"].testSteps["Add"]
def addcontext = new WsdlTestRunContext(addTestStep)

addTestStep.run(testRunner,addcontext)

//teststeps for getting the employee details that was added before

def getreg = testRunner.testCase.testSuite.testCases["Getemployee"].testSteps["get"].getPropertyValue("Request")
def getxmlreg = new XmlHolder(getreg)

getxmlreg.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
def newgetxml = getxmlreg.getXml()

testRunner.testCase.testSuite.testCases["Getemployee"].testSteps["get"].setPropertyValue("Request",newgetxml)

//executing the script
getteststep = testRunner.testCase.testSuite.testCases["Getemployee"].testSteps["get"]
getcontext = new WsdlTestRunContext(getteststep)
getteststep.run(testRunner,getcontext)

//validation of name information of get and add employee

def getresponse = testRunner.testCase.testSuite.testCases["Getemployee"].testSteps["get"].getPropertyValue("Response")
def newgetresponse = new XmlHolder(getresponse)

def responsename = newgetresponse.getNodeValue("//ns:return/ns:name")

log.info responsename
log.info name

assert responsename==name

//delete employee

def delreq = testRunner.testCase.testSuite.testCases["Deleteemployee"].testSteps["delete"].getPropertyValue("Request")
def delxml = new XmlHolder(delreq)

delxml.setNodeValue("//typ:deleteEmployee/typ:employeeName",responsename)
def newdelxml = delxml.getXml()
testRunner.testCase.testSuite.testCases["Deleteemployee"].testSteps["delete"].setPropertyValue("Request",newdelxml)

def deletestep = testRunner.testCase.testSuite.testCases["Deleteemployee"].testSteps["delete"]
def delcontext = new WsdlTestRunContext(deletestep)


deletestep.run(testRunner,delcontext)
